@media screen and (min-width: 1024px) {
    .e-content {
        display: grid;
        grid-template-columns: 3fr 1fr;
        column-gap: 30px;
        position: relative;
    }

    .toc {
        grid-column: 2 / 3;
        grid-row: 1 / span 999;
        align-self: start;
        position: sticky;
        top: 20px;
    }

    .toc::before {
        content: "ON this page";
        background-color: #ffe70e;
        color: #444;
        font-size: 16px;
        font-weight: bold;
        font-style: italic;
        padding: 5px 5px;
        margin-left: 20px;
    }
}

@media screen and (max-width: 767px) {
    .e-content {
        display: grid;
        grid-template-columns: 1fr;
    }

    .toc {
        grid-column: 1 / 2;
    }

    .toc::before {
        content: "ON this page";
        background-color: #ffe70e;
        color: #444;
        font-size: 16px;
        font-weight: bold;
        font-style: italic;
        padding: 5px 5px;
        margin-left: 20px;
    }

}
